import React, { useState } from 'react';

import './project-description.css';

export default function ProjectDescription({data}) {
    const [animationIn, setAnimationIn] = useState(false);
    const [animationOut, setAnimationOut] = useState(false);

    const handleAnimate = () => {
        setAnimationIn(true);

    }


    return (
        <div className='project-description'>
            <div className='client ideasworx'>
                <h3 onClick-={handleAnimate}>{data.client}</h3>
            </div>
            <div className='two-columns'>
                <div className='one-third'>
                    <span>{data.year}</span>
                </div>
                <div className='two-third'>
                    <span>{data.role}</span>
                </div>
            </div>
            <div className='description'>
                <p>
                    {data.description}
                </p>
            </div>
            <a 
            // href={`https://${data.url}`} 
            target='_blank' rel='noopener noreferrer' className={`button animated ${animationIn ? 'fadeIn' : 'fadeOut'} `}>
                Visit Website
            </a>
        </div>
    )
}
