import React, { useEffect, useState } from 'react';
import HorizontalScroll from 'react-scroll-horizontal'

import './pages.css';

import './selected-work.css';
import ProjectDescription from '../components/body/ProjectDescription';
import ProjectImages from '../components/body/ProjectImages';
import { projectsData } from '../utils/projectsData';

export default function SelectedWork({ setActivePage }) {
    const [data] = useState(projectsData)
    const [index, setIndex] = useState(0);
    const [scrollableDiv] = useState(document.getElementById('horizontal-scroll'));
    const [animate, setAnimate] = useState(false);
    const [visibleItems, setVisibleItems] = useState([]);
    const [invisibleItems, setInvisibleItems] = useState([]);

    useEffect(() => {
        setActivePage("Selected Work")
    }, [])

    const handleAnimate = () => {
        setAnimate(!animate);

        if (document.body.scrollTop > 200 || document.documentElement.scrollTop > 200) {
            setVisibleItems()
        }  
    }

    return (
        <div id='horizontal-scroll' className='body-container selected-work'>
            {/* <HorizontalScroll onScroll={onScroll} reverseScroll={true} animValues={3}> */}
            <div onScrollY={handleAnimate} className='slider-container'>
                {data.map((project, i) => (
                    <div className='projects-container' key={project.id}
                        // style={{ marginTop: `calc(${i} * 10px)`, marginLeft: `calc(${i} * 10px)`, opacity: `calc((${i} * 0.1) + 1)`}}
                    >
                        {/* <a href='#' onClick={previousSlide} className='slider-arrow left'>
                            <span className='arrow left' />
                        </a> */}
                        <div className='project-images'>
                            <ProjectImages animate={animate} data={project}/>
                        </div>
                        <ProjectDescription animate={animate} data={project}/>
                        {/* <a href='#' onClick={nextSlide} className='slider-arrow right'>
                            <span className='arrow right' />
                        </a> */}
                    </div>
                ))}
            </div> 
            {/* </HorizontalScroll> */}
        </div>
    )
}
