import React, { useState, useEffect } from 'react';
import HorizontalScroll from 'react-scroll-horizontal'

import './pages.css';

import './selected-work.css';
import ProjectDescription from '../components/body/ProjectDescription';
import ProjectImages from '../components/body/ProjectImages';
import { projectsData } from '../utils/projectsData';

export default function SelectedWork() {
    const [data] = useState(projectsData)
    const [index, setIndex] = useState(0);
    const [currentProject, setCurrentProject] = useState(data[index])
    const [scrollableDiv] = useState(document.getElementById('horizontal-scroll'));

    useEffect(() => {
        setCurrentProject(data[index]);
    }, [index])

    useEffect(() => {

    }, [])

    return (
        <div onScroll={() => console.log(scrollableDiv.scrollLeft)} id='horizontal-scroll' className='body-container selected-work'>
            <HorizontalScroll reverseScroll={true} animValues={3}>
                {data.map((project, i) => (
                    <div className='projects-container'
                        // style={{ marginTop: `calc(${i} * 10px)`, marginLeft: `calc(${i} * 10px)`, opacity: `calc((${i} * 0.1) + 1)`}}
                    >
                        {/* <a href='#' onClick={previousSlide} className='slider-arrow left'>
                            <span className='arrow left' />
                        </a> */}
                        <ProjectImages data={project}/>
                        <ProjectDescription data={project}/>
                        {/* <a href='#' onClick={nextSlide} className='slider-arrow right'>
                            <span className='arrow right' />
                        </a> */}
                    </div>
                ))}
            </HorizontalScroll>
        </div>
    )
}
