import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";

import './App.css';
import './utils/variables.css';

import About from './pages/About';
import SelectedWork from './pages/SelectedWork';

import Header from './components/header/Header';

function App() {
  return (
    <Router>
      <div className="App">
        <Header/>
      </div>

      <Switch>
        <Route exact path='/'>
          <SelectedWork />
        </Route>
          <Route path="/about">
            <About />
          </Route>
        </Switch>
    </Router>
  );
}

export default App;
