import React from 'react';
import FadeIn from 'react-fade-in';

import './sidebar.css';

export default function SideBar({ pageName, language, setLanguage }) {
    return (
        <div className='sidebar'>
            <div className='page-name'>
                <FadeIn delay={50}><h2>{pageName === "Selected Work" ? "Work" : "About"}</h2></FadeIn>
            </div>
            <div className='language-switcher'>
                <div className={language === "Fr" ? 'en' : 'en active'} onClick={() => setLanguage("En")}>
                    En
                </div>
                <div className={language === "En" ? 'fr' : 'fr active'} onClick={() => setLanguage("Fr")}>
                    Fr
                </div>
            </div>
        </div>
    )
}
