import React, { useState } from 'react';
import FadeIn from 'react-fade-in';

import './project-description.css';

export default function ProjectDescription({ data, animate }) {
    // const [animationIn, setAnimationIn] = useState(false);


    return (
        <div className='project-description'>
            <div className='client ideasworx'>
                <FadeIn delay={50}><h3 className={animate ? 'fadeOut' : ''}>{data.client}</h3></FadeIn>
            </div>
            <div className='two-columns'>
                <div className='one-third'>
                    <FadeIn delay={50}><span className={animate ? 'fadeOut' : ''}>{data.year}</span></FadeIn>
                </div>
                <div className='two-third'>
                    <FadeIn delay={50}><span className={animate ? 'fadeOut' : ''}>{data.role}</span></FadeIn>
                </div>
            </div>
            <div className='description'>
                <p className={animate ? 'fadeOut' : ''}>
                    <FadeIn delay={50}>{data.description}</FadeIn>
                </p>
            </div>
            <a 
            // href={`https://${data.url}`}
            target='_blank' rel='noopener noreferrer' className='button'>
                Visit Website
            </a>
        </div>
    )
}
