import {
  BrowserRouter as Router,
  Switch,
  Route,
} from "react-router-dom";
import AnimatedCursor from "react-animated-cursor"
import { useState } from 'react';

import './App.css';
import './utils/variables.css';

import About from './pages/About';
import SelectedWork from './pages/SelectedWork';

import Header from './components/header/Header';
import SideBar from './components/header/SideBar';

function App() {
  // const { pageName } = useParams();
  const [activePage, setActivePage] = useState("Selected Work");
  const [language, setLanguage] = useState("En");

  return (
    <Router>
      <div className="App">
        <Header pageName={activePage} setPageName={setActivePage} language={language}/>
        <SideBar pageName={activePage} setPageName={setActivePage} language={language} setLanguage={setLanguage}/>
        <AnimatedCursor
          innerSize={10}
          outerSize={10}
          color='237, 92, 41'
          outerAlpha={0.2}
          innerScale={0.7}
          outerScale={5}
        />

        <Switch>
          <Route exact path='/'>
            <SelectedWork language={language}/>
          </Route>
            <Route path="/about">
              <About setActivePage={setActivePage} language={language}/>
            </Route>
        </Switch>
      </div>
{/* 
        <Switch>
          <Route path="/:pageName" children={<Child />} />
        </Switch> */}
    </Router>
  );
}

export default App;
