import React, { useState } from 'react';

import './project-description.css';

export default function ProjectDescription({data}) {
    // const [animationIn, setAnimationIn] = useState(false);
    const [animation, setAnimation] = useState(false);

    const handleAnimate = () => {
        setAnimation(true); 
    }


    return (
        <div className='project-description'>
            <div className='client ideasworx'>
                <h3 className={`${animation ? 'fadeOut' : ''}`}>{data.client}</h3>
            </div>
            <div className='two-columns'>
                <div className='one-third'>
                    <span>{data.year}</span>
                </div>
                <div className='two-third'>
                    <span>{data.role}</span>
                </div>
            </div>
            <div className='description'>
                <p>
                    {data.description}
                </p>
            </div>
            <a 
            // href={`https://${data.url}`}
            onClick-={handleAnimate} 
            target='_blank' rel='noopener noreferrer' className='button'>
                Visit Website
            </a>
        </div>
    )
}
