import React from 'react';
import FadeIn from 'react-fade-in';
import { withTranslation } from 'react-i18next';

import './sidebar.css';

export default withTranslation()(function SideBar({ i18n, pageName }) {
    // const { i18n } = useTranslation();

    const choseLanguage = (language) => {
        i18n.changeLanguage(language)
    }

    return (
        <div className='sidebar'>
            <div className='page-name'>
                <FadeIn delay={50}><h2>{pageName === "Selected Work" ? "Work" : "About"}</h2></FadeIn>
            </div>
            <div className='language-switcher'>
                <div 
                    onClick={() => choseLanguage('en')} 
                    // className={language === "Fr" ? 'en' : 'en active'}
                    className='en'
                >
                    En
                </div>
                <div 
                    onClick={() => choseLanguage('fr')} 
                    // className={language === "En" ? 'fr' : 'fr active'}
                    className='fr'
                >
                    Fr
                </div>
            </div>
        </div>
    )
})
