import React, { useEffect, useState } from 'react';

import './project-list-screen.css';

export default function ProjectImages({ data, currentProjectIndex }) {
    useEffect(() => {
        return (
            <video className='video-player' autoPlay loop muted key={i}>
                <source src={data[currentProjectIndex].interface.mobile} type='video/mp4' />
            </video>  
        )
    }, [currentProjectIndex])

    return (
        <React.Fragment>
            <div data-depth='0.1' className='images-container mobile no-noise'>
                {data.interface.mobile.map((img, i) => (
                        <video className='video-player' autoPlay loop muted key={i}>
                            <source src={img} type='video/mp4' />
                        </video>
                    ))}
                <div className='screen'/>
            </div>
            </React.Fragment>
    )
}
